                                                    `type`="number", 
                                                    `visible`="(auc)"))))
                                        self$add(jmvcore::Image$new(
                                            options=options,
                                            name="rocPlot",
                                            title="ROC Curve",
                                            width=450,
                                            height=400,
                                            renderFun=".rocPlot",
                                            visible="(rocPlot)",
                                            refs="ROCR",
                                            clearWith=list(
                                                "dep",
                                                "blocks",
                                                "refLevels")))}))$new(options=options))}))$new(options=options)))
            self$add(jmvcore::Output$new(
                options=options,
                name="predictOV",
                title="Predicted values",
                measureType="continuous",
                clearWith=list(
                    "dep",
                    "blocks",
                    "refLevels")))
            self$add(jmvcore::Output$new(
                options=options,
                name="residsOV",
                title="Residuals",
                measureType="continuous",
                clearWith=list(
                    "dep",
                    "blocks",
                    "refLevels")))
            self$add(jmvcore::Output$new(
                options=options,
                name="cooksOV",
                title="Cook's distance",
                measureType="continuous",
                clearWith=list(
                    "dep",
                    "blocks",
                    "refLevels")))}))

