ttestISBase <- if (requireNamespace("jmvcore", quietly=TRUE)) R6::R6Class(
    "ttestISBase",
    inherit = jmvcore::Analysis,
    public = list(
        initialize = function(options, data=NULL, datasetId="", analysisId="", revision=0) {
            super$initialize(
                package = "jmv",
                name = "ttestIS",
                version = c(1,0,0),
                options = options,
                results = ttestISResults$new(options=options),
                data = data,
